<?php?><?php // display the admin options pagefunction umlauts_options_page() {print '<div class="wrap"><h2>'.UMLAUTS_PUGIN_NAME ." ". UMLAUTS_CURRENT_VERSION. '<sub>(Build '.UMLAUTS_CURRENT_BUILD.')</sub></h2><form action="options.php" method="post">';settings_fields('umlauts_options');do_settings_sections('plugin');$options = get_option('umlauts_options');if ($_GET['settings-updated'] && $options['error']) {	print '<div class="error">'.$options['message'].'</div>';}print '<br /><input name="Submit" type="submit" value="'.esc_attr__('Save Changes').'" /></form></div>';}?>